package day2.pagi;

public class MateriWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Perulangan cetak nama 5 kali
		int i = 0;
		while(i<5) {
			System.out.println("yykw");
			i++;
		}
		i=0;
		do {
			System.out.println("Yudha");
			i++;
		}while(i<5);
		
		for(i=0;i<5;i++) {
			System.out.println("XSis Academy");
		}
		
	}

}
